import { useState } from "react";
import { Navigate, useNavigate } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const history = useNavigate();

  async function handleSubmit(event) {
    event.preventDefault();

    try {
      await fetch("../data.php", {
        method: "POST",
        body: formData
    })
    .then((response) => response.json())
    .then((result) => {
        document.getElementById("icon").className = "";
        document.getElementById("icon").className = result.icon + " stats-icon color";
        document.getElementById("data").innerText = result.data;
        document.getElementById("text").innerText = result.text;
       
        var colors = document.getElementsByClassName('color');
        for(var i = 0 ; i < colors.length ; i++) {
            colors[i].style.color = result.color;
        }
    });



      const response = await fetch("http://localhost/rakugaki_server/login-register/login.php", {
        method: "POST",
        mode: 'no-cors',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: email, password: password }),
      }).then((response) => console.log(response))
      
      if (!response.ok) {
        throw new Error("Invalid email or password.");
      }

      // Redirect to the dashboard page on successful login
      history.push("/dashboard");
    } catch (error) { 
      setError(error.message);
    }
  }

  return (
    <form onSubmit={handleSubmit}>
      {error && <div>{error}</div>}
      <label>
        Email:
        <input
          type="email"
          value={email}
          onChange={(event) => setEmail(event.target.value)}
          required
        />
      </label>
      <br />
      <label>
        Password:
        <input
          type="password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
          required
        />
      </label>
      <br />
      <button type="submit">Login</button>
    </form>
  );
}

export default Login;
