import React, { Component } from 'react';
import '../css/rakugaki_draw.css';
import '../Components/rakugakidraw.js';
import { Navbar } from 'react-bootstrap';
import NavBanner from '../Components/NavBanner';
import drawinglogo from '../assets/rakugakidraw_logosvg.svg';
import ToolBar from '../Components/ToolBar';

import Pen_icon from '../assets/pen_icon.png';
import Eraser_icon from '../assets/eraser_icon.png';
import Small_icon from '../assets/small_size_icon.png';
import Large_icon from '../assets/big_size_icon.png';


  

class DrawingCanvas extends Component {
    
    componentDidMount() {
        const canvas = document.getElementById('canvas');
        const ctx = canvas.getContext('2d');
        ctx.fillStyle = 'white';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        const undoButton = document.getElementById('undo');
        undoButton.addEventListener('click', undo);
        const redoButton = document.getElementById('redo');
        redoButton.addEventListener('click', redo); 
        let isDrawing = false;
        let currentColor = 'black';
        let currentSize = 10;
        let currentTool = 'pen';
        canvas.addEventListener('mousedown', startDrawing);
        canvas.addEventListener('mousemove', draw);
        canvas.addEventListener('mouseup', stopDrawing);
        canvas.addEventListener('mouseout', stopDrawing);
        const controls = document.getElementById('controls');
        controls.addEventListener('click', setTool); 
        function startDrawing(event) {
          isDrawing = true;
          ctx.beginPath();
          ctx.moveTo(event.offsetX, event.offsetY);
        }
      
      function draw(event) {
        if (isDrawing) {
          ctx.lineTo(event.offsetX, event.offsetY);
          ctx.strokeStyle = currentColor;
          ctx.lineWidth = currentSize;
          ctx.stroke();
          states.push({
            image: canvas.toDataURL(),
            stroke: {
              color: currentColor,
              width: currentSize,
              x: event.offsetX,
              y: event.offsetY
            }
          });
          currentState++;
        }
      }
      
      function stopDrawing() {
        isDrawing = false;
      }
      
      function setTool(event) {
        if (event.target.id === 'pen') {
          currentTool = 'pen';
          currentColor = 'black';
        } else if (event.target.id === 'eraser') {
          currentTool = 'eraser';
          currentColor = 'white';
        } else if (event.target.id === 'small') {
          if(currentTool === 'pen') currentColor = 'black';
          currentSize = 10;
        } else if (event.target.id === 'medium') {
          if(currentTool === 'pen') currentColor = 'black';
          currentSize = 20;
        } else if (event.target.id === 'large') {
          if(currentTool === 'pen') currentColor = 'black';
          currentSize = 30;
        } else if (event.target.id === 'clear') {
          ctx.fillStyle = 'white';
          ctx.fillRect(0, 0, canvas.width, canvas.height);
        } else if (event.target.id === 'download') {
          downloadCanvas();
        }
      }
      
      function downloadCanvas() {
        const link = document.createElement('a');
        link.download = 'image.jpg';
        link.href = canvas.toDataURL('image/jpeg');
        link.click();
      }
        
        //=====================================================
        
      let currentState = 0;
      let states = [];
        
        // Push the initial state of the canvas onto the states array
      states.push(canvas.toDataURL());
        
      function undo() {
        if (currentState > 0) {
          currentState--;
          // Redraw the previous state of the canvas, but remove the last stroke
          const image = new Image();
          image.src = states[currentState].image;
          image.onload = function() {
            ctx.drawImage(image, 0, 0);
          };
        }
      }
        
      function redo() {
        if (currentState < states.length - 1) {
          currentState++;
          const image = new Image();
          image.src = states[currentState].image;
          image.onload = function() {
            ctx.drawImage(image, 0, 0);
          };
        }
      }
    }
    render() {
      return (
          <div id='background'>
            <NavBanner/>
            <ToolBar/>
            <img id='logodraw' src={drawinglogo}/>
              <div id="controls">
                  <button class="tool" id="pen"><img id="icon" src={Pen_icon}></img></button>
                  <button class="tool" id="eraser"><img id="icon" src={Eraser_icon}></img></button>
                  <button class="tool" id="small"><img id="icon" src={Small_icon}></img></button>
                  <button class="tool" id="medium"><img id="icon" src={Medium_icon}></img></button>
                  <button class="tool" id="large">Large</button>
                  <button class="tool" id="clear">Clear</button>
                  <button class="tool" id="undo">Undo</button>
                  <button class="tool" id="redo">Redo</button>
                  <button class="tool" id="download">Download</button>
              </div>
              <div class="container">
                  <canvas id="canvas" width="500" height="300"></canvas>
              </div>
          </div>
          
      );
}}


export default DrawingCanvas;
