import '../custom.scss'
import React, {useEffect } from "react";
import { useState } from 'react';
import video from '../assets/video_banner.webm';
import '../css/Welcome.css';
import '../css/Modal.css';
import Modal from '../Components/loginmodal';
import Modalsignin from '../Components/signinmodal';
import Footer from '../Components/Footer';
import NavBanner from '../Components/NavBanner';

window.onload = function() {bannerload()};
function bannerload(){
  document.getElementById("slideshow").style.height = "105vh";
  document.getElementById("slideshow").style.width = "100%";
  document.getElementById("slideshow").style.marginTop = "0%";
  document.getElementById("slideshow").style.zIndex = "1000";
  document.getElementById("navbar").style.zIndex = "-1";
  document.getElementById("title_outside").style.display = 'none'
}

window.onscroll = function() {scrollFunction()};
function scrollFunction() {
  if (document.body.scrollTop > 50 || document.documentElement.scrollTop > 50) {
    document.getElementById("slideshow").style.height = "50vh";
    // document.getElementById("slideshow").style.width = "80%";
    document.getElementById("slideshow").style.marginTop = "5%";
    document.getElementById("slideshow").style.zIndex = "-1";
    document.getElementById("navbar").style.zIndex = "1000";
    document.getElementById("title_outside").style.display = 'block'
  } else {
    document.getElementById("slideshow").style.height = "120vh";
    // document.getElementById("slideshow").style.width = "100%";
    document.getElementById("slideshow").style.marginTop = "0%";
    document.getElementById("slideshow").style.zIndex = "1000";
    document.getElementById("navbar").style.zIndex = "-1";
    document.getElementById("title_outside").style.display = 'none'
  }
}

function Welcome() {
  useEffect(() => {
    window.scrollTo(0, 0);
}, []);
  const [openModal, setOpenModal] = useState(false);
  const [openSigninModal, setOpenSigninModal] = useState(false);
  useEffect(() => {
    document.body.style.overflow = openModal ? "hidden" : "scroll";
    return () => {
      document.body.style.overflow = "scroll";
    };
  }, [openModal]);


  return (
    <>

    </>

  );
}

export default Welcome;
