import axios from 'axios';
import { data } from 'jquery';
import { useState } from 'react';
import '../custom.scss'
import { Navigate, useNavigate } from 'react-router-dom';

const Register=()=>{

    let history = useNavigate();
    const [data, setData]=useState({
        first_name:"",
        last_name:"",
        email:"",
        password:""
    })

    const handleChange=(e)=>{
        setData({ ...data, [e.target.name]: e.target.value });

        console.log(data);
    }

    const submitForm=(e)=>{
        e.preventDefault();
        const sendData = {
            first_name:data.first_name,
            last_name:data.last_name,
            data:data.email,
            password:data.password
        }

        console.log(sendData);

        axios.post('http://localhost/rakugaki_server/login-register/insert.php',sendData)
        .then((result)=>{
            if (result.data.Status == 'Invalid')
                alert ('Invalid User');
            else
                Navigate('/MainApp');
        })

    }


    return(
        <div>
            <form onSubmit={submitForm}>
                <div className="row">
                    <div className="col-md-12 text-center"><h1>Register</h1></div>
                </div>
                    <div className="row">
                        <div className="col-md-6">FirstName</div>
                        <div className="col-md-6">
                            <input type="text" name="first_name"
                                onChange={handleChange} value={data.first_name}
                            ></input>
                        </div>
                    </div>

                    <div className="row">
                        <div className="col-md-6">LastName</div>
                        <div className="col-md-6">
                            <input type="text" name="last_name"
                                onChange={handleChange} value={data.last_name}
                            ></input>
                        </div>
                    </div>

                    <div className="row">
                        <div className="col-md-6">email</div>
                        <div className="col-md-6">
                            <input type="email" name="email"
                                onChange={handleChange} value={data.email}
                            ></input>
                        </div>
                    </div>

                    <div className="row">
                        <div className="col-md-6">password</div>
                        <div className="col-md-6">
                            <input type="password" name="password"
                                onChange={handleChange} value={data.password}
                            ></input>
                        </div>
                    </div>

                    <div className="row">
                        <div className="col-md-12 text-center">
                            <input type="submit" name="submit" value="register"></input>
                        </div>
                    </div>

                </form>
            
        </div>
    )
}

export default Register;
