import React from "react";
import NavBanner from "../Components/NavBanner";
import CarouselBanner from '../Components/Carousel';
import ToolBar from "../Components/ToolBar";
import PlaceholderPost from "../Components/Placeholderpost";
import { useState } from "react";
import '../css/user_account.css';
import { useEffect } from "react";



function Accountpage(){
    const [user, setUser] = useState(null);
    useEffect(() => {
        const userStr = localStorage.getItem('user');
        if (userStr) {
          setUser(JSON.parse(userStr));
        }
      }, []);
    return(
        <>
            <NavBanner/>

            <div class="user_banner_wrap" id="user_01"></div>
            <div class="user_profile_pic" id="user_profile_01"></div>
            <div class='user_info'>
                <h1>{user.first_name}</h1>
                <h2>@ {user.last}</h2>
                <div class="edit_button"><h3>Edit Profile</h3></div>
                <p>I draw a lot of things...</p>
                <hr></hr>
            </div>            
            <PlaceholderPost/>

            <ToolBar/>

        </>
    )
}

export default Accountpage;
