      <div class="slide" id='slide'>
        <div></div>
        <div></div>
        <div></div>
        <div></div>
        <div></div>
      </div>
      <div id='title_outside'>
        <p>RAKUGAKI</p>
        <h1>ラク<br></br>ガキ</h1>
      </div>

      <section id='buttons'>
      <button type="button" class="btn btn-primary" id='button_signin' data-toggle="button" aria-pressed="false" autocomplete="off" onClick={() => setOpenSigninModal(true)}>
          sign in
        </button>
        <button type="button" class="btn btn-primary" id='button_login' data-toggle="button" aria-pressed="false" autocomplete="off" onClick={() => setOpenModal(true)}>
          log in
        </button>
      </section>
      {openModal && <Modal closeModal={setOpenModal}/>}
      {openSigninModal && <Modalsignin closeModal={setOpenSigninModal} />}

      <Footer/>
    </>

  );
}

export default Welcome;
