import axios from 'axios';
import { useState } from 'react';
import { Navigate, useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();

  const [user, setUser] = useState({ email: '', password: '' });

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => { // Renamed submitForm to handleSubmit and made it asynchronous
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost/rakugaki_server/login-register/login.php', user);
      const { email, first_name, last_name } = response.data;

      // Store user data in local storage
      window.localStorage.setItem('email', email);
      window.localStorage.setItem('userName', `${first_name} ${last_name}`);

      navigate('/dashboard');
    } catch (error) {
      console.error(error);
      alert('An error occurred. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="main-box">
        <div className="row m-1">
          <div className="col-md-12 text-center">
            <h1>Login Page</h1>
          </div>
        </div>
        <div className="row m-1">
          <div className="col-md-6">Email:</div>
          <div className="col-md-6">
            <input
              type="email"
              name="email"
              value={user.email}
              onChange={handleChange}
              className="form-control"
            />
          </div>
        </div>
        <div className="row m-1">
          <div className="col-md-6">Password:</div>
          <div className="col-md-6">
            <input
              type="password"
              name="password"
              value={user.password}
              className="form-control"
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="row m-1">
          <div className="col-md-12 text-center p-3">
            <input
              type="submit"
              name="submit"
              className="btn btn-primary"
              value="Please Login"
            />
          </div>
        </div>
      </div>
    </form>
  );
};

export default Login;
