import '../custom.scss'
import React, { useEffect, useState } from "react";
import video from '../assets/video_banner.mp4';
import '../css/Welcome.css';
import '../css/Modal.css';
import Modal from '../Components/Modal';
import Modalsignin from '../Components/SecondModal';
import Footer from '../Components/Footer';
import NavBanner from '../Components/NavBanner';


window.onscroll = function() {scrollFunction()};

function scrollFunction() {
  if (document.body.scrollTop > 50 || document.documentElement.scrollTop > 50) {
    document.getElementById("single").style.height = "50vh";
    document.getElementById("single").style.width = "80%";
    document.getElementById("single").style.marginTop = "5%";
    document.getElementById("title_inside").style.display = 'none';
  } else {
    document.getElementById("single").style.height = "100vh";
    document.getElementById("single").style.width = "100%";
    document.getElementById("single").style.marginTop = "0%";
    document.getElementById("title_inside").style.display = 'block';
  }
}

function Welcome() {
  const [openModal, setOpenModal] = useState(false);
  const [openSigninModal, setOpenSigninModal] = useState(false);

  useEffect(() => {
    document.body.style.overflow = openModal || openSigninModal ? "hidden" : "auto";
    return () => {
      document.body.style.overflow = "auto";
    };
  }, [openModal, openSigninModal]);

  return (
    <>
      <div id="single">
        <NavBanner />
        <section id='banner'>
          <div id='slideshow'>
            <video autoPlay loop muted> 
              <source type="video/mp4" src={video} />
            </video>
          </div>
        </section>
        <div id='title_outside'>
          <p>RAKUGAKI</p>
          <h1>ラク<br></br>ガキ</h1>
        </div>

        <section id='buttons'>
          <button type="button" class="btn btn-primary" id='button_signin' data-toggle="button" aria-pressed="false" autocomplete="off" onClick={() => setOpenSigninModal(true)}>
            sign in
          </button>
          <button type="button" class="btn btn-primary" id='button_login' data-toggle="button" aria-pressed="false" autocomplete="off" onClick={() => setOpenModal(true)}>
            log in
          </button>
        </section>

        {openModal && <Modal closeModal={setOpenModal}/>}
        {openSigninModal && <Modalsignin closeModal={setOpenSigninModal} />}

        <Footer />
        <div class='terms'>
          <a href="" >Terms of Service</a>
          <a href="" >Privacy Policy</a>
        </div>
      </div>

      <style>
        /* Add any additional styles here */
      </style>
    </>
  );
}

export default Welcome;
