import '../custom.scss';
import React, { useEffect, useRef, useState } from 'react';
import { Modal } from 'react-bootstrap';
import video from '../assets/video_banner.mp4';
import '../css/Welcome.css';
import '../css/Modal.css';

function Welcome() {
  const [openModal, setOpenModal] = useState(false);
  const [openSigninModal, setOpenSigninModal] = useState(false);
  const singleRef = useRef(null);

  useEffect(() => {
    const single = singleRef.current;

    const scrollFunction = () => {
      if (single.scrollTop > 50) {
        single.querySelector('#slideshow').style.height = '50vh';
        single.querySelector('#slideshow').style.width = '80%';
        single.querySelector('#slideshow').style.marginTop = '5%';
        single.querySelector('#title_inside').style.display = 'none';
      } else {
        single.querySelector('#slideshow').style.height = '100vh';
        single.querySelector('#slideshow').style.width = '100%';
        single.querySelector('#slideshow').style.marginTop = '0%';
        single.querySelector('#title_inside').style.display = 'block';
      }
    };

    single.addEventListener('scroll', scrollFunction);

    return () => {
      single.removeEventListener('scroll', scrollFunction);
    };
  }, []);

  useEffect(() => {
    document.body.style.overflow = openModal ? 'hidden' : 'scroll';
    return () => {
      document.body.style.overflow = 'scroll';
    };
  }, [openModal]);

  return (
    <div id="single" dir="rtl" ref={singleRef}>
      {/* ... */}
    </div>
  );
}

export default Welcome;
