import React, { useEffect, useRef } from "react";

function Loadingscreen() {
  const titleRef = useRef(null);
  const buttonRef = useRef(null);

  useEffect(() => {
    const titleChars = titleRef.current.children;
    const buttonChars = buttonRef.current.children;

    titleChars.forEach((char, index) => {
      char.style.animationDelay = `${index * 50}ms`;
    });

    buttonChars.forEach((char, index) => {
      char.style.animationDelay = `${index * 50}ms`;
    });

    setTimeout(() => {
      animateloading();
    }, 2000);
  }, []);

  function animateloading() {
    const loadingScreen = document.getElementById("loadingscreen");
    loadingScreen.style.transform = "translateX(-200vw)";
  }

  return (
    <div id="loadingscreen">
      <span className="loadingtext" ref={titleRef}>
        This is
      </span>
      <span className="loadingtext" ref={titleRef}>
        a long
      </span>
      <span className="title" ref={titleRef}>
        long title
      </span>
    </div>
  );
}

export default Loadingscreen;
